function reverse(str) {

    var arr = str.split("");
    var temp = [];
    var count = 0
    for (var i = arr.length - 1; i > -1; i--) {

        //   console.log(arr[i])
        temp[count] = arr[i];
        count++;
    }

    return temp

}


function i_reverse(string) {

    var arr = string.split("");
    var count = arr.length - 1
    for (var i = 0; i < (arr.length / 2); i++) {
        var temp = arr[i];
        arr[i] = arr[i + count];
        arr[i + count] = temp;
        count = count - 2

    }


    return arr;
}


